function lowestProduct(input) {
    const arrayOfNumbers = input.split('')
    const stringLength = arrayOfNumbers.length
    const numberOfIteration = stringLength - 3
    let lowestProduct = 1

    const sumFour = (arr)=>{
        let sum = 0
        for (let i = 0; i < 4; i++) {
            sum += arr[i]
        }
        return sum
    }


    if(stringLength < 4) {
        return  "Number is too small"
    } else {
        for (let i = 0; i < numberOfIteration; i++) {
            lowestProduct = sumFour(input.slice(i,i+3)) < lowestProduct ? sumFour(input.slice(i,i+3)) : lowestProduct
        }
        return  lowestProduct
    }
}